import React, { useState, useEffect, useCallback, useContext } from 'react';
import Statistics from './components/Statistics';
import SearchBar from './components/SearchBar';
import EditModal from './components/EditModal';
import LoginPage from './components/LoginPage';
import { AuthContext } from './context/AuthContext';
import * as api from './services/api';
import {
  AppBar,
  Box,
  Button,
  CircularProgress,
  Container,
  Tabs,
  Tab,
  Toolbar,
  Typography
} from '@mui/material';
import './App.css';

function App() {
  const { isAuthenticated, user, logout, loading } = useContext(AuthContext);

  const [activeTab, setActiveTab] = useState(0); // Tabs use a numeric index
  const [inspirations, setInspirations] = useState([]);
  const [knowledge, setKnowledge] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [goals, setGoals] = useState([]);
  const [categories, setCategories] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);

  const [editModal, setEditModal] = useState({ show: false, type: '', data: null });

  const [inspirationForm, setInspirationForm] = useState({ content: '', tags: '' });
  const [knowledgeForm, setKnowledgeForm] = useState({ title: '', content: '', category: '', source: '' });
  const [taskForm, setTaskForm] = useState({ title: '', description: '', priority: 'medium', due_date: '' });
  const [goalForm, setGoalForm] = useState({ title: '', description: '', type: 'weekly', target_date: '' });
  const [newCategory, setNewCategory] = useState('');

  const apiMap = {
    inspirations: api.inspirations,
    knowledge: api.knowledge,
    tasks: api.tasks,
    goals: api.goals,
  };

  const tabMap = ['inspirations', 'knowledge', 'tasks', 'goals', 'statistics'];

  const fetchInspirations = useCallback(async () => {
    try {
      const response = await api.inspirations.getAll();
      setInspirations(response.data);
    } catch (error) {
      console.error('Error fetching inspirations:', error);
    }
  }, []);

  const fetchKnowledge = useCallback(async () => {
    try {
      const response = await api.knowledge.getAll();
      setKnowledge(response.data);
    } catch (error) {
      console.error('Error fetching knowledge:', error);
    }
  }, []);

  const fetchTasks = useCallback(async () => {
    try {
      const response = await api.tasks.getAll();
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  }, []);

  const fetchGoals = useCallback(async () => {
    try {
      const response = await api.goals.getAll();
      setGoals(response.data);
    } catch (error) {
      console.error('Error fetching goals:', error);
    }
  }, []);

  const fetchCategories = useCallback(async () => {
    try {
      const response = await api.categories.getAll();
      setCategories(response.data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  }, []);

  const fetchAllData = useCallback(() => {
    fetchInspirations();
    fetchKnowledge();
    fetchTasks();
    fetchGoals();
    fetchCategories();
  }, [fetchInspirations, fetchKnowledge, fetchTasks, fetchGoals, fetchCategories]);

  useEffect(() => {
    if (isAuthenticated) {
      fetchAllData();
    }
  }, [isAuthenticated, fetchAllData]);

  const handleSearch = async (searchTerm) => {
    if (!searchTerm.trim()) {
      setIsSearching(false);
      setSearchResults([]);
      return;
    }
    try {
      const response = await api.search.run(searchTerm);
      setSearchResults(response.data);
      setIsSearching(true);
    } catch (error) {
      console.error('Error searching:', error);
    }
  };

  // ... (other handlers remain the same)

  if (loading) {
    return (
        <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
            <CircularProgress />
        </Box>
    );
  }

  if (!isAuthenticated) {
    return <LoginPage />;
  }

  const renderContent = () => {
    if (isSearching) {
        // Search results rendering will be refactored later
        return <div>Search Results...</div>
    }

    switch (tabMap[activeTab]) {
        case 'inspirations':
            return <div>Inspirations Content...</div>; // Placeholder
        case 'knowledge':
            return <div>Knowledge Content...</div>; // Placeholder
        case 'tasks':
            return <div>Tasks Content...</div>; // Placeholder
        case 'goals':
            return <div>Goals Content...</div>; // Placeholder
        case 'statistics':
            return <Statistics />;
        default:
            return null;
    }
  }

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      <AppBar position="sticky">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            个人成长管理
          </Typography>
          <SearchBar onSearch={handleSearch} />
          <Box sx={{ ml: 2, display: 'flex', alignItems: 'center' }}>
            <Typography sx={{ mr: 2 }}>你好, {user.username}</Typography>
            <Button color="inherit" onClick={logout}>注销</Button>
          </Box>
        </Toolbar>
        <Tabs 
            value={activeTab} 
            onChange={(e, newValue) => setActiveTab(newValue)} 
            centered
            indicatorColor="secondary"
            textColor="inherit"
        >
          <Tab label="灵感记录" />
          <Tab label="知识学习" />
          <Tab label="任务管理" />
          <Tab label="目标规划" />
          <Tab label="成长统计" />
        </Tabs>
      </AppBar>

      <Container component="main" sx={{ py: 4, flexGrow: 1 }}>
        {renderContent()}
      </Container>

      {/* The EditModal will be refactored in the next step */}
      {editModal.show && (
        <EditModal
          type={editModal.type}
          data={editModal.data}
          categories={categories}
          onSave={() => {}} // Placeholder
          onClose={() => setEditModal({ show: false, type: '', data: null })}
        />
      )}
    </Box>
  );
}

export default App;
