import axios from 'axios';

const API_BASE = 'http://localhost:3001/api';

// 通用的请求函数
const request = {
  get: (url) => axios.get(`${API_BASE}${url}`),
  post: (url, data) => axios.post(`${API_BASE}${url}`, data),
  put: (url, data) => axios.put(`${API_BASE}${url}`, data),
  delete: (url) => axios.delete(`${API_BASE}${url}`),
};

// 灵感 API
export const inspirations = {
  getAll: () => request.get('/inspirations'),
  create: (data) => request.post('/inspirations', data),
  update: (id, data) => request.put(`/inspirations/${id}`, data),
  delete: (id) => request.delete(`/inspirations/${id}`),
};

// 知识 API
export const knowledge = {
  getAll: () => request.get('/knowledge'),
  create: (data) => request.post('/knowledge', data),
  update: (id, data) => request.put(`/knowledge/${id}`, data),
  delete: (id) => request.delete(`/knowledge/${id}`),
};

// 任务 API
export const tasks = {
  getAll: () => request.get('/tasks'),
  create: (data) => request.post('/tasks', data),
  update: (id, data) => request.put(`/tasks/${id}`, data),
  delete: (id) => request.delete(`/tasks/${id}`),
};

// 目标 API
export const goals = {
  getAll: () => request.get('/goals'),
  create: (data) => request.post('/goals', data),
  update: (id, data) => request.put(`/goals/${id}`, data),
  delete: (id) => request.delete(`/goals/${id}`),
};

// 分类 API
export const categories = {
  getAll: () => request.get('/categories'),
  create: (data) => request.post('/categories', data),
};

// 搜索 API
export const search = {
  run: (searchTerm) => request.get(`/search?q=${encodeURIComponent(searchTerm)}`),
};

// 统计 API
export const statistics = {
  get: () => request.get('/statistics'),
};
