import React, { useState, useEffect } from 'react';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement } from 'chart.js';
import { Pie, Bar } from 'react-chartjs-2';
import axios from 'axios';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

const Statistics = () => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchStatistics();
  }, []);

  const fetchStatistics = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/statistics');
      setStats(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching statistics:', error);
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading">Âä†ËΩΩÁªüËÆ°Êï∞ÊçÆ‰∏≠...</div>;
  }

  if (!stats) {
    return <div className="error">Êó†Ê≥ïÂä†ËΩΩÁªüËÆ°Êï∞ÊçÆ</div>;
  }

  const taskData = {
    labels: ['Â∑≤ÂÆåÊàê', 'Êú™ÂÆåÊàê'],
    datasets: [
      {
        data: [stats.tasks.completed, stats.tasks.total - stats.tasks.completed],
        backgroundColor: ['#28a745', '#dc3545'],
        borderWidth: 2,
      },
    ],
  };

  const overviewData = {
    labels: ['ÁÅµÊÑüËÆ∞ÂΩï', 'Áü•ËØÜÂ≠¶‰π†', '‰ªªÂä°ÊÄªÊï∞', 'ÁõÆÊ†áÊÄªÊï∞'],
    datasets: [
      {
        label: 'Êï∞Èáè',
        data: [stats.inspirations, stats.knowledge, stats.tasks.total, stats.goals.total],
        backgroundColor: ['#007bff', '#28a745', '#ffc107', '#17a2b8'],
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'bottom',
      },
    },
  };

  return (
    <div className="tab-content">
      <h2>ÊàêÈïøÁªüËÆ°</h2>
      <div className="statistics-grid">
        <div className="stat-card">
          <h3>ÊÄª‰ΩìÊ¶ÇËßà</h3>
          <div className="stat-numbers">
            <div className="stat-item">
              <span className="stat-label">ÁÅµÊÑüËÆ∞ÂΩï</span>
              <span className="stat-value">{stats.inspirations}</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">Áü•ËØÜÂ≠¶‰π†</span>
              <span className="stat-value">{stats.knowledge}</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">‰ªªÂä°ÂÆåÊàêÁéá</span>
              <span className="stat-value">{stats.tasks.percentage}%</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">ÁõÆÊ†áÂπ≥ÂùáËøõÂ∫¶</span>
              <span className="stat-value">{stats.goals.averageProgress}%</span>
            </div>
          </div>
        </div>

        <div className="stat-card">
          <h3>Êï∞ÊçÆÊ¶ÇËßà</h3>
          <div className="chart-container">
            <Bar data={overviewData} options={chartOptions} />
          </div>
        </div>

        <div className="stat-card">
          <h3>‰ªªÂä°ÂÆåÊàêÊÉÖÂÜµ</h3>
          <div className="chart-container">
            <Pie data={taskData} options={chartOptions} />
          </div>
          <div className="task-summary">
            <p>ÊÄª‰ªªÂä°Êï∞: {stats.tasks.total}</p>
            <p>Â∑≤ÂÆåÊàê: {stats.tasks.completed}</p>
            <p>ÂÆåÊàêÁéá: {stats.tasks.percentage}%</p>
          </div>
        </div>

        <div className="stat-card">
          <h3>ÊàêÈïøËΩ®Ëøπ</h3>
          <div className="growth-summary">
            <div className="growth-item">
              <span className="growth-icon">üí°</span>
              <div>
                <h4>ÁÅµÊÑüÁÅ´Ëä±</h4>
                <p>Â∑≤ËÆ∞ÂΩï {stats.inspirations} ‰∏™ÂàõÊÑèÊÉ≥Ê≥ï</p>
              </div>
            </div>
            <div className="growth-item">
              <span className="growth-icon">üìö</span>
              <div>
                <h4>Áü•ËØÜÁßØÁ¥Ø</h4>
                <p>Â∑≤Â≠¶‰π† {stats.knowledge} ‰∏™Áü•ËØÜÁÇπ</p>
              </div>
            </div>
            <div className="growth-item">
              <span className="growth-icon">‚úÖ</span>
              <div>
                <h4>‰ªªÂä°ÊâßË°å</h4>
                <p>ÂÆåÊàê‰∫Ü {stats.tasks.completed} ‰∏™‰ªªÂä°</p>
              </div>
            </div>
            <div className="growth-item">
              <span className="growth-icon">üéØ</span>
              <div>
                <h4>ÁõÆÊ†áËææÊàê</h4>
                <p>Âπ≥ÂùáËøõÂ∫¶ {stats.goals.averageProgress}%</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Statistics;
